// Step1.js
import React from 'react';
import './Step1.css';

const Step1 = ({ handlePrevStep, handleNextStep, selectedDish, hoveredDish, handleMouseOver, handleMouseOut, handleDishSelection, dishes }) => {
  return (
    <div className="step1">
      <h2>Шаг 1: Выбор блюда</h2>
      <div className="dishes">
        {dishes.map((dish) => (
          <div
            key={dish.id}
            className={`dish ${selectedDish && selectedDish.id === dish.id ? 'selected' : ''}`}
            onClick={() => handleDishSelection(dish)}
            onMouseOver={() => handleMouseOver(dish)}
            onMouseOut={handleMouseOut}
          >
            <img src={dish.src} alt={dish.title} />
            {hoveredDish && hoveredDish.id === dish.id && (
              <div className="dish-description">
                <h3>{dish.title}</h3>
                <p>{dish.description}</p>
              </div>
            )}
          </div>
        ))}
      </div>
      <button onClick={handlePrevStep}>Назад</button>
      <button onClick={handleNextStep}>Далее</button>
    </div>
  );
};

export default Step1;
