import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './FamilyPage.css';

const FamilyPage = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedDish, setSelectedDish] = useState(null);
  const [selectedCook, setSelectedCook] = useState(null);
  const [selectedMeal, setSelectedMeal] = useState(null);

  const handleNextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handlePrevStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  const handleDishSelection = (dish) => {
    setSelectedDish(dish);
  };

  const handleCookSelection = (cook) => {
    setSelectedCook(cook);
  };

  const handleMealSelection = (meal) => {
    setSelectedMeal(meal);
  };

  const handleAddDish = () => {
    // логика добавления блюда
  };

  const handleDeleteDish = () => {
    // логика удаления блюда
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const formatDate = (date) => {
    const today = new Date();
    if (date.toDateString() === today.toDateString()) {
      return 'Блюда на сегодня';
    }
    return `Блюда на ${date.toLocaleDateString('ru-RU')}`;
  };

  return (
    <div className="family-page">
      <div className="calendar-container">
        <Calendar onChange={handleDateChange} value={selectedDate} />
      </div>
      <div className="form-container">
        <h2>{formatDate(selectedDate)}</h2>
        {currentStep === 1 && (
          <div>
            <p>Завтрак: {selectedDish}</p>
            <p>Обед: {selectedDish}</p>
            <p>Ужин: {selectedDish}</p>
            <button onClick={handleDeleteDish}>Удалить</button>
            <button onClick={handleNextStep}>Добавить</button>
          </div>
        )}
        {currentStep === 2 && (
          <div>
            <h2>Шаг 1: Выбор блюда</h2>
            {/* Пример блюд */}
            <div className="dishes">
              <div onMouseOver={() => handleDishSelection('Борщ')} onClick={() => handleDishSelection('Борщ')}>Борщ</div>
              <div onMouseOver={() => handleDishSelection('Пельмени')} onClick={() => handleDishSelection('Пельмени')}>Пельмени</div>
              {/* Другие блюда */}
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 3 && (
          <div>
            <h2>Шаг 2: Кто будет готовить</h2>
            <div className="cooks">
              <div onClick={() => handleCookSelection('Мама')}>
                <img src="/path/to/mom.jpg" alt="Мама" className={`cook ${selectedCook === 'Мама' ? 'selected' : ''}`} />
              </div>
              <div onClick={() => handleCookSelection('Папа')}>
                <img src="/path/to/dad.jpg" alt="Папа" className={`cook ${selectedCook === 'Папа' ? 'selected' : ''}`} />
              </div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 4 && (
          <div>
            <h2>Шаг 3: Выбор приема пищи</h2>
            <div className="meals">
              <div onClick={() => handleMealSelection('Завтрак')}>Завтрак</div>
              <div onClick={() => handleMealSelection('Обед')}>Обед</div>
              <div onClick={() => handleMealSelection('Ужин')}>Ужин</div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleAddDish}>Добавить блюдо</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FamilyPage;
