// Step2.js
import React from 'react';
import './Step2.css';

const Step2 = ({ handlePrevStep, handleNextStep, selectedCook, handleCookSelection }) => {
  return (
    <div className="step2">
      <h2>Шаг 2: Кто будет готовить</h2>
      <div className="cooks">
        <div className={`cook ${selectedCook === 'Мама' ? 'selected' : ''}`} onClick={() => handleCookSelection('Мама')}>
          <img src="/path/to/mom.jpg" alt="Мама" />
        </div>
        <div className={`cook ${selectedCook === 'Папа' ? 'selected' : ''}`} onClick={() => handleCookSelection('Папа')}>
          <img src="/path/to/dad.jpg" alt="Папа" />
        </div>
      </div>
      <button onClick={handlePrevStep}>Назад</button>
      <button onClick={handleNextStep}>Далее</button>
    </div>
  );
};

export default Step2;
