import React, { useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import { FaBars } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const isMobile = useMediaQuery({ query: '(max-width: 1000px)' });
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="header">
      <div className="container">
        <div className="nav-container">
          <div className="logo">
            <img src="/logo.png" alt="Logo" className="logo-image" />
          </div>
          {isMobile ? (
            <>
              <div className="mobile-menu-container">
                <FaBars className="burger-icon" onClick={toggleMenu} />
                {menuOpen && (
                  <nav className="mobile-menu">
                    <Link to="/">Меню</Link>
                    <Link to="/">Рецепты</Link>
                    <Link to="/">Семья</Link>
                    <Link to="/auth" className="button">Войти</Link>
                    <Link to="/register" className="button">Зарегистрироваться</Link>
                  </nav>
                )}
              </div>
            </>
          ) : (
            <>
              <nav className="nav-links">
                <Link to="/">Меню</Link>
                <Link to="/">Рецепты</Link>
                <Link to="/">Семья</Link>
              </nav>
              <Link to="/" className="title">A Cooking Schedule</Link>
              <div className="buttons">
                <Link to="/auth" className="button">Войти</Link>
                <Link to="/register" className="button">Зарегистрироваться</Link>
              </div>
            </>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
