import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Recipe.css';

const Recipe = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3001/api/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the recipe!', error);
      });
  }, [id]);

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div className="recipe-container">
      <img src={recipe.src} alt={recipe.name} className="recipe-image" />
      <div className="recipe-details">
        <h1 className="recipe-name">{recipe.name}</h1>
        <p className="recipe-description">{recipe.description}</p>
        <div className="recipe-section">
          <h2>Ingredients</h2>
          <p>{recipe.ingredients}</p>
        </div>
        <div className="recipe-section">
          <h2>Full Recipe</h2>
          <p>{recipe.title}</p>
        </div>
        <div className="recipe-section nutrition">
          <h2>Nutrition</h2>
          <div className="nutrition-details">
            <div>
              <strong>Calories:</strong> {recipe.calories}
            </div>
            <div>
              <strong>Carbohydrates:</strong> {recipe.carbohydrates}
            </div>
            <div>
              <strong>Fats:</strong> {recipe.fats}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Recipe;
