import React from 'react';
import { useParams } from 'react-router-dom';
import './Recipe.css';

const recipes = {
    1: { src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
    2: { src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
    3: { src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
    4: { src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
    5: { src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
    6: { src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
    7: { src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
    8: { src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
    9: { src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
    10: { src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
    11: { src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
    12: { src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
    13: { src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
    14: { src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
    15: { src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
    16: { src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
    17: { src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
    18: { src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
    19: { src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
    20: { src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
    21: { src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' }
  };

const Recipe = () => {
  const { id } = useParams();
  const recipe = recipes[id];

  return (
    <div className="recipe-container">
      <img src={recipe.src} alt={recipe.title} className="recipe-image" />
      <div className="recipe-details">
        <h1>{recipe.title}</h1>
        <p>{recipe.description}</p>
      </div>
    </div>
  );
};

export default Recipe;
