// src/components/daymenu/DayMenu.js
import React from 'react';
import { Link } from 'react-router-dom';
import './DayMenu.css';

const DayMenu = ({ index, day, meals }) => {
  return (
    <div className="day-menu">
      <h2>{day}</h2>
      <div className="meal-columns">
        <div className="meal-time">
          <h3>Завтрак</h3>
          <div className="meals">
            {meals.breakfast.map((meal) => (
              <Link to={`/recipe/${index}`} className="meal" key={meal.id}>
                <img src={meal.src} alt={meal.title} className="meal-image" />
                <div className="meal-info">
                  <h4>{meal.title}</h4>
                  <p>{meal.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
        <div className="meal-time">
          <h3>Обед</h3>
          <div className="meals">
            {meals.lunch.map((meal) => (
              <Link to={`/recipe/${meal.id}`} className="meal" key={meal.id}>
                <img src={meal.src} alt={meal.title} className="meal-image" />
                <div className="meal-info">
                  <h4>{meal.title}</h4>
                  <p>{meal.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
        <div className="meal-time">
          <h3>Ужин</h3>
          <div className="meals">
            {meals.dinner.map((meal) => (
              <Link to={`/recipe/${meal.id}`} className="meal" key={meal.id}>
                <img src={meal.src} alt={meal.title} className="meal-image" />
                <div className="meal-info">
                  <h4>{meal.title}</h4>
                  <p>{meal.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DayMenu;
