// src/components/Gallery.js
import React from 'react';
import './Gallery.css';

const Gallery = () => {
    const images = [
      { src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
      { src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
      { src: '/olivier.jpg', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
      { src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
      { src: '/shchi.jpg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
      { src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
      { src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' }
    ];

    return (
      <div className="gallery">
        {images.map((image, index) => (
          <div className="gallery-item" key={index}>
            <img src={image.src} alt={image.title} className="gallery-image" />
            <h3>{image.title}</h3>
            <p>{image.description}</p>
          </div>
        ))}
      </div>
    );
  };

export default Gallery;
