// InitialStep.js
import React from 'react';
import './InitialStep.css';

const InitialStep = ({ selectedDate, handleNextStep, handleDeleteDish }) => {
  return (
    <div className="initial-step">
      <h2>Блюда на {selectedDate.toLocaleDateString('ru-RU')}</h2>
      <div className="meals-ste">
        <div className="meal">
          <p>Завтрак: ...</p>
        </div>
        <div className="meal">
          <p>Обед: ...</p>
        </div>
        <div className="meal">
          <p>Ужин: ...</p>
        </div>
      </div>
      <div className="buttons">
        <button onClick={handleDeleteDish}>Удалить</button>
        <button onClick={handleNextStep}>Добавить</button>
      </div>
    </div>
  );
};

export default InitialStep;
