// src/components/weekmenu/WeekMenu.js
import React from 'react';
import DayMenu from '../daymenu/DayMenu';
import './WeekMenu.css';

const weekMeals = [
  {
    day: 'Понедельник',
    meals: [
      { src: '/borscht.jpg', title: 'Завтрак: Борщ', description: 'Традиционный украинский суп с буряком' },
      { src: '/pelmeni.jpeg', title: 'Обед: Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
      { src: '/olivier.png', title: 'Ужин: Салат Оливье', description: 'Классический салат с колбасой и овощами' },
    ],
  },
  {
    day: 'Вторник',
    meals: [
      { src: '/blini.jpg', title: 'Завтрак: Блины', description: 'Тонкие русские блины с начинкой' },
      { src: '/shchi.jpeg', title: 'Обед: Щи', description: 'Русский суп из капусты и мяса' },
      { src: '/vareniki.jpg', title: 'Ужин: Вареники', description: 'Тесто с картофельной или творожной начинкой' },
    ],
  },
  {
    day: 'Среда',
    meals: [
      { src: '/kulebyaka.jpg', title: 'Завтрак: Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
      { src: '/borscht.jpg', title: 'Обед: Борщ', description: 'Традиционный украинский суп с буряком' },
      { src: '/pelmeni.jpeg', title: 'Ужин: Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
    ],
  },
  {
    day: 'Четверг',
    meals: [
      { src: '/olivier.png', title: 'Завтрак: Салат Оливье', description: 'Классический салат с колбасой и овощами' },
      { src: '/blini.jpg', title: 'Обед: Блины', description: 'Тонкие русские блины с начинкой' },
      { src: '/shchi.jpeg', title: 'Ужин: Щи', description: 'Русский суп из капусты и мяса' },
    ],
  },
  {
    day: 'Пятница',
    meals: [
      { src: '/vareniki.jpg', title: 'Завтрак: Вареники', description: 'Тесто с картофельной или творожной начинкой' },
      { src: '/kulebyaka.jpg', title: 'Обед: Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
      { src: '/borscht.jpg', title: 'Ужин: Борщ', description: 'Традиционный украинский суп с буряком' },
    ],
  },
  {
    day: 'Суббота',
    meals: [
      { src: '/pelmeni.jpeg', title: 'Завтрак: Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
      { src: '/olivier.png', title: 'Обед: Салат Оливье', description: 'Классический салат с колбасой и овощами' },
      { src: '/blini.jpg', title: 'Ужин: Блины', description: 'Тонкие русские блины с начинкой' },
    ],
  },
  {
    day: 'Воскресенье',
    meals: [
      { src: '/shchi.jpeg', title: 'Завтрак: Щи', description: 'Русский суп из капусты и мяса' },
      { src: '/vareniki.jpg', title: 'Обед: Вареники', description: 'Тесто с картофельной или творожной начинкой' },
      { src: '/kulebyaka.jpg', title: 'Ужин: Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
    ],
  },
];

const WeekMenu = () => {
  return (
    <div className="week-menu">
      {weekMeals.map((dayMeal, index) => (
        <DayMenu key={index} day={dayMeal.day} meals={dayMeal.meals} />
      ))}
    </div>
  );
};

export default WeekMenu;
