import React, { useEffect, useState } from 'react';
import DayMenu from '../daymenu/DayMenu';
import axios from 'axios';
import './WeekMenu.css';

const WeekMenu = () => {
  const [weekMeals, setWeekMeals] = useState([]);

  useEffect(() => {
    const fetchWeekMeals = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/weekmeals');
        setWeekMeals(response.data.weekMeals);
      } catch (error) {
        console.error('Ошибка при получении недельного меню:', error);
      }
    };

    fetchWeekMeals();
  }, []);

  return (
    <div className="week-menu">
      {weekMeals.map((dayMeal, index) => (
        <DayMenu key={index} day={dayMeal.day} meals={dayMeal.meals} />
      ))}
    </div>
  );
};

export default WeekMenu;
