import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './FamilyPage.css';

const dishes = [
  { id: 1, src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
  { id: 2, src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
  { id: 3, src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
  { id: 4, src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
  { id: 5, src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
  { id: 6, src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
  { id: 7, src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
  { id: 8, src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
  { id: 9, src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
  { id: 10, src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
  { id: 11, src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
  { id: 12, src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
  { id: 13, src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
  { id: 14, src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
  { id: 15, src: '/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком' },
  { id: 16, src: '/pelmeni.jpeg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде' },
  { id: 17, src: '/olivier.png', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами' },
  { id: 18, src: '/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой' },
  { id: 19, src: '/shchi.jpeg', title: 'Щи', description: 'Русский суп из капусты и мяса' },
  { id: 20, src: '/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой' },
  { id: 21, src: '/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой' },
];

const FamilyPage = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedDish, setSelectedDish] = useState(null);
  const [selectedCook, setSelectedCook] = useState(null);
  const [selectedMeal, setSelectedMeal] = useState(null);
  const [hoveredDish, setHoveredDish] = useState(null);
  const [showTooltip, setShowTooltip] = useState(false);

  const handleNextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handlePrevStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  const handleDishSelection = (dish) => {
    setSelectedDish(dish);
  };

  const handleCookSelection = (cook) => {
    setSelectedCook(cook);
  };

  const handleMealSelection = (meal) => {
    setSelectedMeal(meal);
  };

  const handleAddDish = () => {
    // логика добавления блюда
    setCurrentStep(1);
  };

  const handleDeleteDish = () => {
    // логика удаления блюда
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const formatDate = (date) => {
    const today = new Date();
    if (date.toDateString() === today.toDateString()) {
      return 'Блюда на сегодня';
    }
    return `Блюда на ${date.toLocaleDateString('ru-RU')}`;
  };

  const handleMouseOver = (dish) => {
    const timeout = setTimeout(() => {
    setShowRecipe(dish);
    }, 2000);
    setHoverTimeout(timeout);
    };

    const handleMouseOut = () => {
    clearTimeout(hoverTimeout);
    setShowRecipe(null);
    };

  return (
    <div className="family-page">
      <div className="calendar-container">
        <Calendar 
          onChange={handleDateChange} 
          value={selectedDate} 
          tileClassName={({ date, view }) =>
            // Добавляем класс для выбранной даты
            date.toDateString() === selectedDate.toDateString() ? 'selected-date' : ''
          }
        />
      </div>
      <div className="form-container">
        <h2>{formatDate(selectedDate)}</h2>
        {currentStep === 1 && (
          <div>
            <p>Завтрак: {selectedDish && <img src={selectedDish.src} alt={selectedDish.title} className="dish-image" />}</p>
            <p>Обед: {selectedDish && <img src={selectedDish.src} alt={selectedDish.title} className="dish-image" />}</p>
            <p>Ужин: {selectedDish && <img src={selectedDish.src} alt={selectedDish.title} className="dish-image" />}</p>
            <button onClick={handleDeleteDish}>Удалить</button>
            <button onClick={handleNextStep}>Добавить</button>
          </div>
        )}
        {currentStep === 2 && (
          <div>
            <h2>Шаг 1: Выбор блюда</h2>
            <div className="dishes">
              {dishes.map(dish => (
                <div 
                  key={dish.id} 
                  onMouseOver={() => handleMouseOver(dish)} 
                  onMouseOut={handleMouseOut} 
                  onClick={() => handleDishSelection(dish)}
                  className={`dish ${selectedDish && selectedDish.id === dish.id ? 'selected' : ''}`}
                >
                  <img src={dish.src} alt={dish.title} className="dish-image" />
                  {showTooltip && hoveredDish && hoveredDish.id === dish.id && (
                    <div className="recipe-tooltip">
                      <h3>{hoveredDish.title}</h3>
                      <p>{hoveredDish.description}</p>
                    </div>
                  )}
                </div>
              ))}
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 3 && (
          <div>
            <h2>Шаг 2: Кто будет готовить</h2>
            <div className="cooks">
              <div onClick={() => handleCookSelection('Мама')}>
                <img src="/path/to/mom.jpg" alt="Мама" className={`cook ${selectedCook === 'Мама' ? 'selected' : ''}`} />
              </div>
              <div onClick={() => handleCookSelection('Папа')}>
                <img src="/path/to/dad.jpg" alt="Папа" className={`cook ${selectedCook === 'Папа' ? 'selected' : ''}`} />
              </div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 4 && (
          <div>
            <h2>Шаг 3: Выбор приема пищи</h2>
            <div className="meals">
              <div 
                onClick={() => handleMealSelection('Завтрак')}
                className={`meal ${selectedMeal === 'Завтрак' ? 'selected' : ''}`}
              >
                Завтрак
              </div>
              <div 
                onClick={() => handleMealSelection('Обед')}
                className={`meal ${selectedMeal === 'Обед' ? 'selected' : ''}`}
              >
                Обед
              </div>
              <div 
                onClick={() => handleMealSelection('Ужин')}
                className={`meal ${selectedMeal === 'Ужин' ? 'selected' : ''}`}
              >
                Ужин
              </div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleAddDish}>Добавить блюдо</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FamilyPage;
