// src/components/Recipe.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './Recipe.css';

const recipes = {
  1: { src: 'https://example.com/images/borscht.jpg', title: 'Борщ', description: 'Традиционный украинский суп с буряком. Рецепт: ингредиенты и способ приготовления...' },
  2: { src: 'https://example.com/images/pelmeni.jpg', title: 'Пельмени', description: 'Тесто с мясной начинкой, отваренное в воде. Рецепт: ингредиенты и способ приготовления...' },
  3: { src: 'https://example.com/images/olivier.jpg', title: 'Салат Оливье', description: 'Классический салат с колбасой и овощами. Рецепт: ингредиенты и способ приготовления...' },
  4: { src: 'https://example.com/images/blini.jpg', title: 'Блины', description: 'Тонкие русские блины с начинкой. Рецепт: ингредиенты и способ приготовления...' },
  5: { src: 'https://example.com/images/shchi.jpg', title: 'Щи', description: 'Русский суп из капусты и мяса. Рецепт: ингредиенты и способ приготовления...' },
  6: { src: 'https://example.com/images/vareniki.jpg', title: 'Вареники', description: 'Тесто с картофельной или творожной начинкой. Рецепт: ингредиенты и способ приготовления...' },
  7: { src: 'https://example.com/images/kulebyaka.jpg', title: 'Кулебяка', description: 'Слоеный пирог с мясом и рыбой. Рецепт: ингредиенты и способ приготовления...' }
};

const Recipe = () => {
  const { id } = useParams();
  const recipe = recipes[id];

  return (
    <div className="recipe-container">
      <img src={recipe.src} alt={recipe.title} className="recipe-image" />
      <div className="recipe-details">
        <h1>{recipe.title}</h1>
        <p>{recipe.description}</p>
      </div>
    </div>
  );
};

export default Recipe;
