// Step3.js
import React from 'react';
import './Step3.css';

const Step3 = ({ handlePrevStep, handleAddDish, selectedMeal, handleMealSelection }) => {
  return (
    <div className="step3">
      <h2>Шаг 3: Выбор приема пищи</h2>
      <div className="meals">
        <div className={`meal ${selectedMeal === 'Завтрак' ? 'selected' : ''}`} onClick={() => handleMealSelection('Завтрак')}>
          Завтрак
        </div>
        <div className={`meal ${selectedMeal === 'Обед' ? 'selected' : ''}`} onClick={() => handleMealSelection('Обед')}>
          Обед
        </div>
        <div className={`meal ${selectedMeal === 'Ужин' ? 'selected' : ''}`} onClick={() => handleMealSelection('Ужин')}>
          Ужин
        </div>
      </div>
      <button onClick={handlePrevStep}>Назад</button>
      <button onClick={handleAddDish}>Добавить блюдо</button>
    </div>
  );
};

export default Step3;
