import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './FamilyPage.css';

const dishes = [
  { id: 1, src: '/borscht.jpg', title: 'Борщ' },
  { id: 2, src: '/pelmeni.jpeg', title: 'Пельмени' },
  { id: 3, src: '/olivier.png', title: 'Салат Оливье' },
  { id: 4, src: '/blini.jpg', title: 'Блины' },
  { id: 5, src: '/shchi.jpeg', title: 'Щи' },
  { id: 6, src: '/vareniki.jpg', title: 'Вареники' },
  { id: 7, src: '/kulebyaka.jpg', title: 'Кулебяка' },
  { id: 8, src: '/borscht.jpg', title: 'Борщ' },
  { id: 9, src: '/pelmeni.jpeg', title: 'Пельмени' },
  { id: 10, src: '/olivier.png', title: 'Салат Оливье' },
  { id: 11, src: '/blini.jpg', title: 'Блины' },
  { id: 12, src: '/shchi.jpeg', title: 'Щи' },
  { id: 13, src: '/vareniki.jpg', title: 'Вареники' },
  { id: 14, src: '/kulebyaka.jpg', title: 'Кулебяка' },
  { id: 15, src: '/borscht.jpg', title: 'Борщ' },
  { id: 16, src: '/pelmeni.jpeg', title: 'Пельмени' },
  { id: 17, src: '/olivier.png', title: 'Салат Оливье' },
  { id: 18, src: '/blini.jpg', title: 'Блины' },
  { id: 19, src: '/shchi.jpeg', title: 'Щи' },
  { id: 20, src: '/vareniki.jpg', title: 'Вареники' },
  { id: 21, src: '/kulebyaka.jpg', title: 'Кулебяка' },
];

const FamilyPage = ({ selectedDate, onDateChange }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedDish, setSelectedDish] = useState(null);
  const [selectedCook, setSelectedCook] = useState(null);
  const [selectedMeal, setSelectedMeal] = useState(null);

  const handleNextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handlePrevStep = () => {
    setCurrentStep((prevStep) => prevStep - 1);
  };

  const handleDishSelection = (dish) => {
    setSelectedDish(dish);
  };

  const handleCookSelection = (cook) => {
    setSelectedCook(cook);
  };

  const handleMealSelection = (meal) => {
    setSelectedMeal(meal);
  };

  const handleAddDish = () => {
    setCurrentStep(1); // возвращаемся на первый шаг после добавления блюда
  };

  const handleDeleteDish = () => {
    // логика удаления блюда
  };

  return (
    <div className="family-page">
      <div className="calendar-container">
        <Calendar onChange={onDateChange} value={selectedDate} />
      </div>
      <div className="form-container">
        {currentStep === 1 && (
          <div>
            <h2>Блюда на {selectedDate.toDateString() === new Date().toDateString() ? "сегодня" : selectedDate.toLocaleDateString()}</h2>
            <p>Завтрак: {selectedDish}</p>
            <p>Обед: {selectedDish}</p>
            <p>Ужин: {selectedDish}</p>
            <button onClick={handleDeleteDish}>Удалить</button>
            <button onClick={handleNextStep}>Добавить</button>
          </div>
        )}
        {currentStep === 2 && (
          <div>
            <h2>Шаг 1: Выбор блюда</h2>
            <div className="dishes">
              {dishes.map((dish) => (
                <div key={dish.id} className={`dish ${selectedDish === dish.title ? 'selected' : ''}`} onClick={() => handleDishSelection(dish.title)}>
                  <img src={dish.src} alt={dish.title} className="dish-image" />
                </div>
              ))}
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 3 && (
          <div>
            <h2>Шаг 2: Кто будет готовить</h2>
            <div className="cooks">
              <div onClick={() => handleCookSelection('Мама')}>
                <img src="/path/to/mom.jpg" alt="Мама" className={`cook ${selectedCook === 'Мама' ? 'selected' : ''}`} />
              </div>
              <div onClick={() => handleCookSelection('Папа')}>
                <img src="/path/to/dad.jpg" alt="Папа" className={`cook ${selectedCook === 'Папа' ? 'selected' : ''}`} />
              </div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleNextStep}>Далее</button>
          </div>
        )}
        {currentStep === 4 && (
          <div>
            <h2>Шаг 3: Выбор приема пищи</h2>
            <div className="meals">
              <div className={`meal ${selectedMeal === 'Завтрак' ? 'selected' : ''}`} onClick={() => handleMealSelection('Завтрак')}>Завтрак</div>
              <div className={`meal ${selectedMeal === 'Обед' ? 'selected' : ''}`} onClick={() => handleMealSelection('Обед')}>Обед</div>
              <div className={`meal ${selectedMeal === 'Ужин' ? 'selected' : ''}`} onClick={() => handleMealSelection('Ужин')}>Ужин</div>
            </div>
            <button onClick={handlePrevStep}>Назад</button>
            <button onClick={handleAddDish}>Добавить блюдо</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FamilyPage;
