import React from 'react';
import './InitialStep.css';

const InitialStep = ({ selectedDate, meals, handleNextStep, handleDeleteDish }) => {
  const formatDate = (date) => {
    const today = new Date();
    if (date.toDateString() === today.toDateString()) {
      return 'Блюда на сегодня';
    }
    return `Блюда на ${date.toLocaleDateString('ru-RU')}`;
  };

  return (
    <div className="initial-step">
      <h2>{formatDate(selectedDate)}</h2>
      <div className="meals-initiastep">
        <div className="meal-initiastep">
          <p>Завтрак: {meals.breakfast ? `${meals.breakfast.dish.title} (${meals.breakfast.cook})` : '...'}</p>
        </div>
        <div className="meal-initiastep">
          <p>Обед: {meals.lunch ? `${meals.lunch.dish.title} (${meals.lunch.cook})` : '...'}</p>
        </div>
        <div className="meal-initiastep">
          <p>Ужин: {meals.dinner ? `${meals.dinner.dish.title} (${meals.dinner.cook})` : '...'}</p>
        </div>
      </div>
      <div className="buttons">
        <button onClick={handleDeleteDish}>Удалить</button>
        <button onClick={handleNextStep}>Добавить</button>
      </div>
    </div>
  );
};

export default InitialStep;
