import React from 'react';
import './InitialStep.css';

const InitialStep = ({ selectedDate, meals, handleNextStep, handleDeleteDish }) => {
  console.log('Meals in InitialStep:', meals);

  const formatDate = (date) => {
    const today = new Date();
    if (date.toDateString() === today.toDateString()) {
      return 'Блюда на сегодня';
    }
    return `Блюда на ${date.toLocaleDateString('ru-RU')}`;
  };

  return (
    <div className="initial-step">
      <h2>{formatDate(selectedDate)}</h2>
      <div className="meals-initiastep">
        <div className="meal-initiastep">
          <div className="meal-header">Завтрак:</div>
          {meals.breakfast ? (
            <>
              <img src={meals.breakfast.dish.src} alt={meals.breakfast.dish.title} className="meal-image" />
              <div className="meal-info">
                <div>{meals.breakfast.dish.title}</div>
                <div>({meals.breakfast.cook})</div>
              </div>
            </>
          ) : <div className="no-meal">...</div>}
        </div>
        <div className="meal">
          <div className="meal-header">Обед:</div>
          {meals.lunch ? (
            <>
              <img src={meals.lunch.dish.src} alt={meals.lunch.dish.title} className="meal-image" />
              <div className="meal-info">
                <div>{meals.lunch.dish.title}</div>
                <div>({meals.lunch.cook})</div>
              </div>
            </>
          ) : <div className="no-meal">...</div>}
        </div>
        <div className="mealinitiastep">
          <div className="meal-header">Ужин:</div>
          {meals.dinner ? (
            <>
              <img src={meals.dinner.dish.src} alt={meals.dinner.dish.title} className="meal-image" />
              <div className="meal-info">
                <div>{meals.dinner.dish.title}</div>
                <div>({meals.dinner.cook})</div>
              </div>
            </>
          ) : <div className="no-meal">...</div>}
        </div>
      </div>
      <div className="buttons">
        <button onClick={handleDeleteDish}>Удалить</button>
        <button onClick={handleNextStep}>Добавить</button>
      </div>
    </div>
  );
};

export default InitialStep;
